local DazzleMrG = {} Log.Write("Loaded script: Dazzle.lua")  local myHero = Heroes.GetLocal() local myPlayer = Players.GetLocal() local TimerWards = 1 local TimerTome = 1 local TargetHeal = nil; DazzleMrG.optionEnabled = Menu.AddOptionBool({"MrGarabato", " Выбрать героя","Grave"}, "Enabled", false) DazzleMrG.optionDazzleIcon = Menu.AddOptionIcon({"MrGarabato", " Выбрать героя"}, "panorama/images/heroes/icons/npc_dota_hero_dazzle_png.vtex_c") DazzleMrG.optionDazzleGraveIcon = Menu.AddOptionIcon({"MrGarabato", " Выбрать героя", "Grave"}, "panorama/images/spellicons/dazzle/immortal/dazzle_shallow_grave_png.vtex_c") DazzleMrG.optionDazzleWaveIcon = Menu.AddOptionIcon({"MrGarabato", " Выбрать героя", "Wave"}, "panorama/images/spellicons/dazzle/immortal/dazzle_shallow_grave_png.vtex_c") DazzleMrG.optionCountEnemyGrave = Menu.AddOptionSlider({"MrGarabato", " Выбрать героя", "Grave"}, "Health", 1, 99, 1) DazzleMrG.optionEnabled2 = Menu.AddOptionBool({"MrGarabato", " Выбрать героя","Grave"}, "Ultimate scepter", false)  function DazzleMrG.OnUpdate() 	if not Menu.IsEnabled(MrGarabato_v1_MenuHx.optionEnable) then return true end     if not Menu.IsEnabled(MrGarabato_v1_MenuHx.EnableHero) then return end	 	if Menu.IsEnabled(DazzleMrG.optionEnabled) then 		if NPC.GetUnitName(myHero)=="npc_dota_hero_dazzle" and Entity.IsAlive(myHero) then 			Grave = NPC.GetAbility(myHero, "dazzle_shallow_grave"); 			if Entity.GetHeroesInRadius(myHero, Ability.GetCastRange(Grave), Enum.TeamType.TEAM_FRIEND) then 				DazzleMrG.DazzleGrave(Entity.GetHeroesInRadius(myHero, Ability.GetCastRange(Grave), Enum.TeamType.TEAM_FRIEND),Grave); 			end 			if ((Entity.GetMaxHealth(myHero)*0.01)*Menu.GetValue(DazzleMrG.optionCountEnemyGrave) >= Entity.GetHealth(myHero)) and Entity.GetHeroesInRadius(myHero, 1000, Enum.TeamType.TEAM_ENEMY) and Ability.IsReady(Grave) then 				DazzleMrG.DazzleCastGrave(Grave,myHero); 			end 		end     end end function DazzleMrG.DazzleCastGrave(Grave,Target) 	if(NPC.GetItem(myHero, "item_ultimate_scepter")) then 		Ability.CastPosition(Grave, Entity.GetOrigin(Target), true); 	else 		Ability.CastTarget(Grave,Target,true); 	end end function DazzleMrG.DazzleGrave(HeroesRadius,Grave) 	for i = 1, #HeroesRadius do 		if ((Entity.GetMaxHealth(HeroesRadius[i])*0.01)*Menu.GetValue(DazzleMrG.optionCountEnemyGrave) >= Entity.GetHealth(HeroesRadius[i])) and Entity.GetHeroesInRadius(HeroesRadius[i], 1000, Enum.TeamType.TEAM_ENEMY) and Ability.IsReady(Grave) then 			DazzleMrG.DazzleCastGrave(Grave,HeroesRadius[i]); 		end 	end end return DazzleMrG